mapper.select:
SELECT a.alarm_code alarmCode,a.place_code placeCode,a.alarm_type alarmType,a.alarm_time alarmTime,a.deal_way dealWay,a.oos_url oosUrl,a.video_url videoUrl,a.sign sign,p.place_name placeName  
FROM ${_prefix}biz_alarm a,${_prefix}biz_place p

	a.place_code=p.place_code ${andsql} 

ORDER BY a.alarm_time desc ${maxnum}

SELECT count(a.alarm_code)   
FROM ${_prefix}biz_alarm a,${_prefix}biz_place p

	a.place_code=p.place_code ${andsql}

SELECT aa.alarm_code alarmCode,aa.place_code placeCode,aa.alarm_type alarmType,aa.alarm_time alarmTime,aa.deal_way dealWay,aa.oos_url oosUrl,aa.video_url videoUrl, aa.sign sign,pp.place_name placeName,pp.trade_type tradeType FROM js_biz_alarm aa,js_biz_place pp where aa.place_code=pp.place_code ${andsqlaa}
and ${maxnum}>( select count(a.alarm_code) from js_biz_alarm a,js_biz_place p where a.place_code=p.place_code ${andsqla} and pp.trade_type=p.trade_type and aa.alarm_time < a.alarm_time )
order by aa.alarm_time desc
