mapper.select:
SELECT p.place_code placeCode,p.place_name placeName,p.trade_type tradeType,p.city city,p.area area,p.street street,p.geo_coordinates geoCoordinates, p.representative representative,p.phone phone,p.business_status businessStatus FROM ${_prefix}biz_place p
1=1 ${andsql}
ORDER BY p.place_code

SELECT r.rtsp_url rtspUrl,r.place_code placeCode,r.online online FROM ${_prefix}biz_rtsp_url r
1=1 ${andsql}
ORDER BY r.rtsp_url

SELECT a.alarm_code alarmCode,a.place_code placeCode,a.alarm_type alarmType,a.alarm_time alarmTime,a.deal_way dealWay,a.oos_url oosUrl,a.sign sign FROM ${_prefix}biz_alarm a
1=1 ${andsql}
ORDER BY a.alarm_code
